  if (typeof key !== 'string')
    throw new Errors.InvalidSignature(this.line, args);
  if (options && !this.isObject(options))
    throw new Errors.InvalidSignature(this.line, args);
  if (options) {
    this.defaultValue = options.defaultValue;
    delete options.defaultValue;
